require 'set'


def update_matrix(mat)
    checked = Set[]
    m,n = mat.length, mat[0].length
    queue = []
    dirs = [[1,0],[-1,0],[0,1],[0,-1]]
    (0...m).each do |i|
        (0...n).each do |j|
            if mat[i][j] == 0
                queue << [i,j]
                checked.add([i,j])
            end
        end
    end
    until queue.empty?
        new_queue = []
        queue.each do |ele|
            i,j = ele
            val = mat[i][j]
            dirs.each do |dir|
                i,j = i+dir[0],j+dir[1]
                if (0...m).cover?(i) && (0...n).cover?(j) and !checked.include?([i,j])
                    checked.add([i,j])
                    new_queue << [i,j]
                    mat[i][j] = val + 1
                end
            end
        end
        queue = new_queue
    end
    mat
end

